<script>
var checkText = "<?php echo $img[0]->below_text; ?>";
if (checkText == "below_text") {
  $('.below_text').attr( 'checked', true );
}else{
  $('.above_text').attr( 'checked', true );
}
var align_text = "<?php echo $img[0]->text_align; ?>";
if (align_text == "center") {
  $('.text_center').addClass("addstyle");
}else if (align_text == "right") {
  $('.text_right').addClass("addstyle");
}else{
  $('.text_left').addClass("addstyle");
}
  $(document).ready(function(){
     // Add minus icon for collapse element which is open by default
     $(".collapse.show").each(function(){
       $(this).prev(".card-header").find(".fa").addClass("fa-minus").removeClass("fa-plus");
     });

     // Toggle plus minus icon on show hide of collapse element
     $(".collapse").on('show.bs.collapse', function(){
       $(this).prev(".card-header").find(".fa").removeClass("fa-plus").addClass("fa-minus");
     }).on('hide.bs.collapse', function(){
       $(this).prev(".card-header").find(".fa").removeClass("fa-minus").addClass("fa-plus");
     });
 });
</script>
  <script type="text/javascript">
  $(window).on('load', function () {
    $("#loading").delay(2000).fadeOut("slow");
    $("#overlayer").delay(2000).fadeOut("slow");
  });

////////////////////////////////////////////////////////////////////////////////////////////////


  function processSelectedFiles(fileInput) {
    var files = fileInput.files;

    for (var i = 0; i < files.length; i++) {
      var filnme = files[i].name
      var fileName = filnme.replace(/\.[^/.]+$/, "");
      $('.fileRename').val(fileName);
    }
  }
      	$(".image-checkbox").each(function () {
  		  if ($(this).find('input[type="checkbox"]').first().attr("checked")) {
  		    $(this).addClass('image-checkbox-checked');
  		  }
  		  else {
  		    $(this).removeClass('image-checkbox-checked');
  		  }
  		});

  		// sync the state to the input
  		$(".image-checkbox").on("click", function (e) {
  		  $(this).toggleClass('image-checkbox-checked');
  		  var $checkbox = $(this).find('input[type="checkbox"]');
  		  $checkbox.prop("checked",!$checkbox.prop("checked"))

  		  e.preventDefault();
  		});
      </script>
  </body>
</html>
